mapper.select:
select d.content_id,d.user_id,d.quality_control_id,e.quality_control_description,d.target,d.standard,d.pre_warning_value
from details d inner join quality_control q on d.quality_control_id = q.quality_control_id
where d.content_id = #{contentId} and  d.user_id = #{userId} and d.quality_control_id = #{qualityControlId}

mapper.update:
update details set
                               target = #{target},
                               standard = #{standard},
                               pre_warning_value = #{preWarningValue}
where content_id = #{contentId} and user_id = #{userId} and index_id = #{indexId} and
    quality_control_id = #{qualityControlId}

update details set
    target = #{newTarget}
where content_id = #{contentId} and user_id = #{userId} and index_id = #{indexId} and
    quality_control_id = #{qualityControlId}

update details set
    standard = #{newStandard}
where content_id = #{contentId} and user_id = #{userId} and index_id = #{indexId} and
    quality_control_id = #{qualityControlId}

update details set
    pre_warning_value = #{newPreWarningValue}
where content_id = #{contentId} and user_id = #{userId} and index_id = #{indexId} and
    quality_control_id = #{qualityControlId}

mapper.select:
select d.content_id,d.user_id,d.index_id,d.quality_control_id,q.quality_control_description,d.target,d.standard,d.pre_warning_value
    from details d inner join quality_control q on d.quality_control_id = q.quality_control_id
where d.user_id = #{userId} and d.content_id = #{contentId}
