mapper.select:
select * from details
where content_id = #{contentId} and user_id = #{userId} and
quality_contriol_id = #{qualityControlId}

mapper.update:
update details set
                               target = #{target},
                               standard = #{standard},
                               pre_warning_value = #{preWarningValue}
where content_id = #{contentId} and user_id = #{userId} and
    quality_control_id = #{qualityControlId}

update details set
    target = #{target}
where content_id = #{contentId} and user_id = #{userId} and
    quality_control_id = #{qualityControlId}

